import React, { Component } from 'react';

import { generateTiles } from '../../../redux/actions/tileActions';
import TextInput from '../../../components/Forms/TextInput';
import './GenerateForm.scss';
import { Link } from 'react-router-dom';
import { generate_tiles, generate_tiles2 } from '../../../redux/actions/getColorActions';
import { connect } from 'react-redux';
import { saveColor,saveName } from '../../../redux/actions/saveColorActions';
import Tile from '../../../components/Tile';
import GenerateHistory from './GenerateHistory';
class GenerateForm extends Component {
  constructor() {
    super();

    this.state = {
      row: 0,
      column: 0,
      data: '',
      nameinput:'',
      display:0
    }
  }

  onChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  onGenerate = () => {

    const { row, column } = this.state;
    this.props.generate_tiles(row, column);


  }

  setAllToDefault = () => {
    this.props.generate_tiles({ row: 0, column: 0 });
    setTimeout(() => this.props.generate_tiles(this.state));
  }
  savethiscolor = () => {
    const { getcolors, saveColor, namecolors, savecolors, saveName } = this.props;
    const {nameinput,display,}=this.state;

    namecolors.push(nameinput);
    saveName(namecolors);
    savecolors.push(getcolors);
    //console.log('data',data)
    saveColor(savecolors);
    this.setState({display:0});
    //console.log(savecolors);
    //const {row, column}=this.state;
    //this.props.generate_tiles2(savecolors);
    /*savecolors.map((item,idx)=>{
      this.props.generate_tiles2(item);
    })*/
    this.setAllToDefault();
    return;
  }
  deletetile = () => {
    const { data } = this.state;
    const { savecolors } = this.props;
    savecolors.map((item, idx) => {
      if (item === data) {
        this.setState({ data: '' });
      }
    })
  }
  tiles = (item) => {
    const { data } = this.state;
    this.setState({ data: item });
  }
  handleChange=e=>{
    const {nameinput}=this.state;
    console.log('e.target.value',e.target.value);
    this.setState({nameinput:e.target.value});
    console.log('nametile',nameinput);
  }
  displayinput=()=>{
    const {display}=this.state;
    if (!display) {
      this.setState({display:1})
    }

  }
  render() {
    const { data, row, column, nameinput,display } = this.state;
    const { savecolors,saveName } = this.props;

    return (
      <div className="generate-form">
        <div>
          <TextInput name="row" label="Row" value={row} onChange={this.onChange} />
          <TextInput name="column" label="Column" value={column} onChange={this.onChange} />
          <div className="generate-form__buttons">
            <button className="button" onClick={this.onGenerate}>Generate Tiles</button>
            <button className="button button--grey" onClick={this.setAllToDefault}>Set all to default</button>
            <button className="button button--saveimage" onClick={this.displayinput}>Save image</button>
            {!!display && <div className="input-alert">
            <label className="input-alert-name">Tên image</label>
            <input
            type="text"
            name="input-alert-name"
            //value={nameinput}
            onChange={this.handleChange}
            />
            <button onClick={this.savethiscolor}>Save the name</button>  
            </div>}
          </div>
        </div>
        <div className="history">History

          {!!savecolors && <div className="history_item">
            {savecolors.map((item, idx) =>
              <GenerateHistory
                item={item}
                key={idx}
                idx={idx}
                name={nameinput}

              />
              // <button key={idx} onClick={()=>this.setState({data:item})} >{`Item ${idx}`}</button>               

            )}
          </div>}


        </div>
      </div>
    )
  }
}
const mapStateToProps = state => ({
  getcolors: state.getcolor.data,
  namecolors: state.saveColor.name,
  savecolors: state.saveColor.data
})
export default connect(mapStateToProps, { generate_tiles, saveColor, saveName,generate_tiles2 })(GenerateForm);
