import React,{Component} from 'react';
import {connect} from 'react-redux';
import {generate_tiles2} from '../../../redux/actions/getColorActions';
import {saveColor} from '../../../redux/actions/saveColorActions';
class GenerateHistory extends Component {
    constructor() {
        super();

        this.state = {
          row: 0,
          column: 0,
          data:'',
          display:false
        }
      }

generateitem=()=> {
    const {item}=this.props;
    //savecolors.map {}
    //this.props.generate_tiles2({item})
    const {data,display}=this.state;
    if (display===false) {
        this.setState({data:item});
        this.setState({display:true})
    } else {
        this.setState({display:false});
        this.setState({data:''})
    }
}
deleteitem=()=>{
const {item,savecolors}=this.props;
savecolors.map((data,idx)=>{
    if (data==={item}) {
        data='';
    }    
})
saveColor(savecolors);
console.log('state của redux',savecolors);
console.log('item truyền qua',{item});
}   

    render() {
        const {item,idx}=this.props;
        //var {item}=this.props;
        const {data}=this.state;
        const value='';

        return(
            <div className="generate_history">

                <button key={idx} onClick={this.generateitem}> {`Item ${idx}`}</button> 
                {!!data && <button onClick={this.deleteitem}> delete </button>}
                {!!data && <div className="history_item">
                    {data.map((row,rowidx)=>
                        <div key={rowidx} className="tile-row"> 
                            {row.map((column,columnidx)=>
                                <div 
                                    className="tile" 
                                    key={columnidx}
                                    style={{ backgroundColor: column} }
                  />
                )}
              </div>
                )}

            </div> 
           }
            </div>
        )
    }
}
const mapStateToProps=state => ({
  getcolors:state.getcolor.data,
  savecolors:state.saveColor.data
})
export default connect(mapStateToProps,{saveColor,generate_tiles2})(GenerateHistory);
