import React, { Component } from 'react';
import { connect } from 'react-redux';
import { generate_tiles2 } from '../../../redux/actions/getColorActions';
import { saveColor,saveName } from '../../../redux/actions/saveColorActions';

class GenerateHistory extends Component {
    constructor() {
        super();

        this.state = {
            row: 0,
            column: 0,
            data: '',
            display: false
        }
    }

    generateitem = () => {
        const { item } = this.props;
        //savecolors.map {}
        //this.props.generate_tiles2({item})
        const { data, display } = this.state;
        if (display === false) {
            this.setState({ data: item });
            this.setState({ display: true })
        } else {
            this.setState({ display: false });
            this.setState({ data: '' })
        }
    }

    checkitem = () => {
        const { item, savecolors } = this.props;
        return savecolors !== item
    }
    deleteitem = () => {
        const { item, savecolors,saveName, namecolors,idx,saveColor,name } = this.props;
        const {data} = this.state;

        const updatedData = savecolors.filter((data,idx) => JSON.stringify(data) !== JSON.stringify(item));
        const updatedName = namecolors.filter((data,idx) => JSON.stringify(data) !== JSON.stringify(name));

        saveColor(updatedData);
        saveName(updatedName);
        this.generateitem();

        // console.log('redux state 1', savecolors);
        // saveColor(savecolors.pop());
        // console.log('redux state 2', savecolors);
         return;
    }

    render() {
        const { item, idx ,name} = this.props;
        //var {item}=this.props;
        const { data } = this.state;
        const value = '';

        return (
            <div className="generate_history">

                <button key={idx} onClick={this.generateitem}> {`Item ${name}`}</button>
                {!!data && <button onClick={this.deleteitem}> delete </button>}
                {!!data && <div className="history_item">
                    {data.map((row, rowidx) =>
                        <div key={rowidx} className="tile-row">
                            {row.map((column, columnidx) =>
                                <div
                                    className="tile"
                                    key={columnidx}
                                    style={{ backgroundColor: column }}
                                />
                            )}
                        </div>
                    )}

                </div>
                }
            </div>
        )
    }
}
const mapStateToProps = state => ({
    getcolors: state.getcolor.data,
    namecolors: state.saveColor.name,
    savecolors: state.saveColor.data
})
export default connect(mapStateToProps, { saveColor, generate_tiles2,saveName })(GenerateHistory);
